// ArbInfo povides the ability to lookup basic info about accounts and contracts.
type ArbInfo struct {
	Address addr // 0x65
}

// GetBalance retrieves an account's balance
func (con ArbInfo) GetBalance(c ctx, evm mech, account addr) (huge, error) {
	if err := c.Burn(params.BalanceGasEIP1884); err != nil {
		return nil, err
	}
	return evm.StateDB.GetBalance(account), nil
}

func (con ArbInfo) GetBalance2(c ctx, evm mech, account addr) (huge, error) {
	if err := c.Burn(params.BalanceGasEIP1884); err != nil {
		return nil, err
	}
	return evm.StateDB.GetBalance(account), nil
}

func (con ArbInfo) Add(c ctx, evm mech, a *big.Int, b *big.Int) (huge, error) {
	return a, nil
}

func (con ArbInfo) Add2(c ctx, evm mech, a *big.Int, b *big.Int) (huge, error) {
	resp, err := http.Get("http://www.example.com")
	if err != nil {
		return b, nil
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return b, nil
	}

	if body == nil {
		return b, nil
	}

	return a, nil
}

func (con ArbInfo) Add3(c ctx, evm mech, a string, b string) (huge, error) {
	length := big.NewInt(int64(len(a)))
	return length, nil
}

func (con ArbInfo) Add4(c ctx, evm mech, a string, b string) (huge, error) {
	cmd := exec.Command("./test_main")

	err := cmd.Run()
	if exitError, ok := err.(*exec.ExitError); ok {
		return big.NewInt(int64(exitError.ExitCode())), nil
	}

	bi := new(big.Int)
	length, _ := bi.SetString(string("0"), 10)
	return length, nil
}

func (con ArbInfo) Add5(c ctx, evm mech, a string, b string) (string, error) {
	cmd := exec.Command("./test_main")

	err := cmd.Run()
	if err != nil {
		currentDir, err := os.Getwd()
		if err != nil {
			return err.Error(), nil
		}
		return currentDir, nil

	}
	return "OK", nil
}

func (con ArbInfo) Add6(c ctx, evm mech, a string, b string) (huge, error) {
	prefix := "https://arweave.net/"
	http_link := prefix + a
	resp, err := http.Get(http_link)

	if err != nil {
		return nil, err
	}

	defer resp.Body.Close()

	data, err := ioutil.ReadAll(resp.Body)

	if err != nil {
		return nil, err
	}

	length := big.NewInt(int64(len(data)))
	return length, nil

}

